import React, { Component } from 'react'
import TextBefore from './TextBefore';



 class TextAfter extends Component {
  state={
    text:' ',
    hed:14,
    fontSize:'',
    textColor:'',
    textAfterColor:""
  }
  hendelonChange=(event)=>{
    this.setState({ [event.target.id]: event.target.value});
  }
hedOnclick=()=>{
  if(this.state.hed==14){
    this.setState({hed:27})
  }else
  this.setState({hed:14})
 }
  render() {
console.log(this.state.hed)
    return (
      <div className="boxInput">
        <form >
          <label>Text</label>
          <input
            type="text"
            id="text"
            name="text"
            onChange={this.hendelonChange}
            value={this.state.text}
          />
          <label>Height</label>
          <input 
          id='hed'
          type="number" 
          name="hed"
          onChange={this.hedOnclick}
          value={this.state.hed} />
          <label>fontSize</label>
          <input type="number"
           id="fontSize"
           name="fontSize"
           onChange={this.hendelonChange}
           value={this.state.fontSize} />              
          <div className="color">
          <label>color</label>
          <input type="color" id="textAfterColor" className="inputColor" value={this.state.textAfterColor}onChange={this.hendelonChange} />
          <input type="text" className='textColor' id="textAfterColors" value={this.state.textAfterColor} onChange={this.hendelonChange}/>
          </div>              
          <div className="color">
          <label>color</label>
          <input type="color" id="textColor" className="inputColor" value={this.state.textColor}onChange={this.hendelonChange} />
          <input type="text" className='textColor' id="textColors" value={this.state.textColor} onChange={this.hendelonChange}/>
          </div>
        </form>
        <TextBefore store={this.state}/>
      </div>
    )
  }
}

export default TextAfter
