import React, { Component } from "react";
import Style from 'style-it';
import Authtext from "./Authtext";



class Auth extends Component {
  state = {

    shadow1:"",
    hed1:'',
    textColor1:'',
    textColors1:'',
    shadow2:"",
    hed2:'',
    textColor2:'',
    textColors2:'',
  };

  hendelonChange = (event) => {        
        var shadow2 =''
        for (var x = 0; x < this.state.hed2; x++) {
          shadow2 += (shadow2 ? "," : "") + -x * 1 + "px" + 
              " " + x * 1 + "px" +
                " " + 0 + "px" +
              " " +
              `${this.state.textColor2}`;
          }
          this.setState({shadow2:shadow2})
          var shadow1 =''       
          for (var y = 0; y < this.state.hed1 ; y++) {
            shadow1 += (shadow1 ? "," : "") + -y * 1 + "px" + 
                " " + y * 1 + "px" +
                  " " + 0 + "px" +
                " " +
                `${this.state.textColor1}`;
            }
            this.setState({shadow1:shadow1})
        this.setState({ [event.target.id]: event.target.value});
  };


  render() {          
    return (
    <Style>
{`#pilo {
   transform-style: preserve-3d;
   top: 20%; 
   right: 30%;
   transform: rotate(-28deg)skew(20deg);
   font-family: myFirstFont;
   font-size: ${this.state.fontSize}px;
   font-weight: 700;
   text-shadow:${this.state.shadow2};
   position: absolute;
}

   #pilo::before {
    content:"${this.state.text}";
    font-size: ${this.state.fontSize}px;
    top:0;
    left:0;
    position: absolute;
    text-shadow:${this.state.shadow1};

    }
    input[type="color"]{
    border:none;
    width:5%;
    height:3vh;
    }
    .textColor{
      width:64%;
    }
`}

      <div>

        <form className="boxInput">
          <i className="large material-icons">3d_rotation</i>
          <Authtext/>
          <br />
          <label>height</label>
          <br />
          <input 
          type="number" 
          id="hed1"
           onChange={this.hendelonChange}
            value={this.state.hed1} />
          <br /> 
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor1" value={this.state.textColor1}onChange={this.hendelonChange} />
          <input type="text" id="textColors1" className='textColor' value={this.state.textColor1} onChange={this.hendelonChange}/>
          </div>
          <br />
          <label>height</label>
          <br />
          <input 
          type="number" 
          id="hed2"
           onChange={this.hendelonChange}
            value={this.state.hed2} />
          <br /> 
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor2" value={this.state.textColor2}onChange={this.hendelonChange} />
          <input type="text" id="textColors2" className='textColor' value={this.state.textColor2} onChange={this.hendelonChange}/>
          </div>
          <br />
        </form>
      </div>
      </Style>
    );
  }
}

export default Auth;
