import React, { Component } from 'react'
import TextMiddle from './TextMiddle';

class TextBefore extends Component {
  constructor(props){
    super(props)
    this.state={
      hed3:3,
    textColor3:'',
    textColors3:'',
    }
  }
  hendelonChange = (event) => {

    this.setState({ [event.target.id]: event.target.value});
};
hedOnclick=()=>{
  if(this.state.hed3==3){
    this.setState({hed3:5})
  }else
  this.setState({hed3:3})
 }
  render() {
    const state=this.props
    return (
      <div>
        <br/>
        <form>
        <input 
          type="number" 
          id="hed3"
          onChange={this.hendelonChange}
          value={this.state.hed3} />
          <br /> 
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor3"className="inputColor" value={this.state.textColor3}onChange={this.hendelonChange} />
          <input type="text" id="textColors3" className='textColor' value={this.state.textColor3} onChange={this.hendelonChange}/>
          </div>
          <br />
        </form>
        <TextMiddle state={state}/>
      </div>
    )
  }
}

export default TextBefore
