import React, { Component } from 'react'
import Style from 'style-it';

 class TextMiddle extends Component {
   constructor(props){
     super(props)
     this.state={
      hed2:11,
    textColor:'',
    textColors2:'',
     }
   }
   hendelonChange = (event) => {
    this.setState({ [event.target.id]: event.target.value});
};
hedOnclick=()=>{
  if(this.state.hed2==11){
    this.setState({hed2:19})
  }else
  this.setState({hed2:11})
 }
  render() {
    var shadow =''
    for (var i = 0; i < this.state.hed; i++) {
      shadow += (shadow ? "," : "") + -i * 1 + "px" + 
          " " + i * 1 + "px" +
            " " + 0 + "px" +
          " " +
          `${this.props.state.store.textColor}`;
      }  

        var shadow2 =''
        for (var x = 0; x < this.state.hed2; x++) {
          shadow2 += (shadow2 ? "," : "") + -x * 1 + "px" + 
              " " + x * 1 + "px" +
                " " + 0 + "px" +
              " " +
              `${this.props.state.store.textColor2}`;
          }

          var shadow3 =''       
          for (var y = 0; y < this.state.hed3 ; y++) {
            shadow3 += (shadow1 ? "," : "") + -y * 1 + "px" + 
                " " + y * 1 + "px" +
                  " " + 0 + "px" +
                " " +
                `${this.props.state.store.textColor3}`;
            } 
    return (
      <Style>
      {`#pilo {
        transform: rotate(-28deg)skew(20deg);
        font-size: ${this.props.state.store.fontSize}px;
        text-shadow:${shadow2};   
     }
        #pilo::after {
         content:"${this.props.state.store.text}";
         font-size: ${this.props.state.store.fontSize}px;
         text-shadow:${shadow};

        }
        #pilo::before {
         content:"${this.props.state.store.text}";
         font-size: ${this.props.state.store.fontSize}px;
         text-shadow:${shadow3};

         }

     `}
      <div>
      <div>
          <h1
            className="text"
            id="pilo" 
          >
            {this.props.state.store.text}
          </h1>
        </div>
        <form>

        <br />
          <label>height</label>
          <br />
          <input 
          type="number" 
          onChange={this.hedOnclick} 
            value={this.state.hed2} />
          <br /> 
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor"className="inputColor" value={this.state.textColor}onChange={this.hendelonChange} />
          <input type="text" id="textColors2" className='textColor' value={this.state.textColor} onChange={this.hendelonChange}/>
          </div>
        </form>
        <br/>
      </div>
      </Style>
    )
  }
}

export default TextMiddle
