import React, { Component } from 'react'
import Style from 'style-it';

 class TextMiddle extends Component {
   constructor(props){
     super(props)
     this.state={
      hed2:4,
    textColor2:'',
    textColors2:'',
     }
   }
   hendelonChange = (event) => {
    this.setState({ [event.target.id]: event.target.value});
};
hedOnclick=()=>{
  if(this.state.hed2==4){
    this.setState({hed2:15})
  }else
  this.setState({hed2:4})

 }
  render() {
    let hed=this.props.state.store.hed
      let hed3 =this.props.state1.hed3 +hed
      let hed2 =this.state.hed2 + hed + hed3
    var shadow2 =''
    for (var i = 0; i <= hed2; i++) {
      shadow2 += (shadow2 ? "," : "") + -i * 1 + "px" + 
          " " + i * 1 + "px" +
            " " + 0 + "px" +
          " " +
          `${this.state.textColor2}`;
      }  

        var shadow =''
        for (var x = 0; x <= hed; x++) {
          shadow += (shadow ? "," : "") + -x * 1 + "px" + 
              " " + x * 1 + "px" +
                " " + 0 + "px" +
              " " +
              `${this.props.state.store.textColor}`;
          }

          var shadow3 =''       
          for (var y = 0; y <= hed3 ; y++) {
            shadow3 += (shadow3 ? "," : "") + -y * 1 + "px" + 
                " " + y * 1 + "px" +
                  " " + 0 + "px" +
                " " +
                `${this.props.state1.textColor3}`;
            } 
         console.log(hed,hed2,hed3)   
    return (
      <Style>
      {`#pilo {
        transform: rotate(-28deg)skew(20deg);
        font-size: ${this.props.state.store.fontSize}px;
        text-shadow:${shadow2};
        color:${this.state.textColor2}; 
     }
        #pilo::after {
         content:"${this.props.state.store.text}";
         font-size: ${this.props.state.store.fontSize}px;
         text-shadow:${shadow};
         color:${this.props.state.store.textColor}; 

        }
        #pilo::before {
         content:"${this.props.state.store.text}";
         font-size: ${this.props.state.store.fontSize}px;
         text-shadow:${shadow3};
         color:${this.props.state1.textColor3};
         }

     `}
      <div>
      <div>
          <h1
            className="text"
            id="pilo" 
          >
            {this.props.state.store.text}
          </h1>
        </div>
        <form>

        <br />
          <label>height</label>
          <br />
          <input 
          id="hed2"
          type="number" 
          onChange={this.hedOnclick} 
            value={this.state.hed2} />
          <br /> 
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor2"className="inputColor" value={this.state.textColor2}onChange={this.hendelonChange} />
          <input type="text" id="textColors2" className='textColor' value={this.state.textColor2} onChange={this.hendelonChange}/>
          </div>
        </form>
        <br/>
      </div>
      </Style>
    )
  }
}

export default TextMiddle
