import React, { Component } from 'react'
import Style from 'style-it';

 class Authtext extends Component {
   constructor(props){
     super(props)
     this.state={
      shadow: "",
      text: "",
      hed:'',
      textColor:'',
      fontSize :'',
      textColors:'',
     }
   }
   hendelonChange = (event) => {
    var shadow =''
    for (var i = 0; i < this.state.hed; i++) {
      shadow += (shadow ? "," : "") + -i * 1 + "px" + 
          " " + i * 1 + "px" +
            " " + 0 + "px" +
          " " +
          `${this.state.textColor}`;
      }  
      this.setState({shadow:shadow})
      this.setState({ [event.target.id]: event.target.value});
    }
  render() {
    return (
      <Style>
        {` #pilo::after {
    content:"${this.state.text}";
    font-size: ${this.state.fontSize}px;
    top:0;
    left:0;
    position: absolute;
    text-shadow:${this.state.shadow};

   }`}
 <div>
          <h1
            className="text"
            id="pilo" 
          >
            {this.state.text}
          </h1>
        </div>
<br />
          <label>text</label>
          <br />
          <input
            type="text"
            id="text"
            name="text"
            onChange={this.hendelonChange}
            value={this.state.text}
          />
          <br />
          <br />
          <label>height</label>
          <br />
          <input 
          type="number" 
          id="hed"
          name="hed"
           onChange={this.hendelonChange}
            value={this.state.hed} />
          <br />
          <br />
          <label>fontSize</label>
          <br />
          <input type="number"
           id="fontSize"
           name="fontSize"
           onChange={this.hendelonChange}
            value={this.state.fontSize} />
          <br />  
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor" value={this.state.textColor}onChange={this.hendelonChange} />
          <input type="text" className='textColor' id="textColors" value={this.state.textColor} onChange={this.hendelonChange}/>
          </div>
      </Style>
    )
  }
}

export default Authtext
