import React, { Component } from 'react'

 class TextMiddle extends Component {
   constructor(props){
     super(props)
     this.state={
      hed2:'',
    textColor2:'',
    textColors2:'',
     }
   }
   hendelonChange = (event) => {
    this.setState({ [event.target.id]: event.target.value});
};
hedOnclick=()=>{
  if(this.state.hed2==3){
    this.setState({hed2:5})
  }else
  this.setState({hed2:3})
 }
  render() {
    return (
      <div>
        <form>
        <br />
          <label>height</label>
          <br />
          <input 
          type="number" 
          onClick={this.hedOnclick}
           defaultValue={this.state.hed2} 
            value={this.state.hed2} />
          <br /> 
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor2"className="inputColor" value={this.state.textColor2}onChange={this.hendelonChange} />
          <input type="text" id="textColors2" className='textColor' value={this.state.textColor2} onChange={this.hendelonChange}/>
          </div>
        </form>

      </div>
    )
  }
}

export default TextMiddle
