import React, { Component } from 'react'
import TextBefore from './TextBefore';
import ColourWheel from './colourWheel/ColourWheel'

const yourDefaultColour = 'rgb(255, 255, 255)'
const yourDefaultColour1 = 'rgb(255, 255, 255)'

 class TextAfter extends Component {
  state={
    text:' ',
    hed:14,
    fontSize:'',
    textColor:'',
    selectedColour1: yourDefaultColour1,
    selectedColour: yourDefaultColour,
  }
  hendelonChange=(event)=>{
    this.setState({ [event.target.id]: event.target.value});
  }
  clearColourWheel = () => {
    this.colourWheel.clear(() => {
      // Do some other stuff in this callback if you want -- other than re-setting your selectedColour.
      this.setState({ selectedColour: yourDefaultColour })
    })
  }

hedOnclick=()=>{
  if(this.state.hed==14){
    this.setState({hed:27})
  }else
  this.setState({hed:14})
 }
  render() {
    const { selectedColour } = this.state
    console.log(this.state.selectedColour)
    return (
      <div className="boxInput">
        <form >
          <label>Text</label>
          <input
            type="text"
            id="text"
            name="text"
            onChange={this.hendelonChange}
            value={this.state.text}
          />
          <label>Height</label>
          <input 
          id='hed'
          type="number" 
          name="hed"
          onChange={this.hedOnclick}
          value={this.state.hed} />
          <label>fontSize</label>
          <input type="number"
           id="fontSize"
           name="fontSize"
           onChange={this.hendelonChange}
           value={this.state.fontSize} />              
          <div className="color">
          <label>color</label>
          <input type="color" id="textAfterColor" className="inputColor" value={this.state.textAfterColor}onChange={this.hendelonChange} />
          <input type="text" className='textColor' id="textAfterColors" value={this.state.textAfterColor} onChange={this.hendelonChange}/>
          </div>              
          <ColourWheel
          id="selectedColour"
          radius={200}
          padding={10}
          lineWidth={50}
          onChange={this.hendelonChange}
          onColourSelected={(rgb) => this.setState({ selectedColour: rgb })}
          onRef={ref => (this.colourWheel = ref)}
          spacers={{
            colour: '#FFFFFF',
            shadowColour: 'grey',
            shadowBlur: 5
          }}
          preset // You can set this bool depending on whether you have a pre-selected colour in state.
          presetColour={this.state.selectedColour}
          animated
        />
          <ColourWheel
          id="selectedColour1"
          radius={200}
          padding={10}
          lineWidth={50}
          onChange={this.hendelonChange}
          onColourSelected={(rgb) => this.setState({ selectedColour1: rgb })}
          onRef={ref => (this.colourWheel = ref)}
          spacers={{
            colour: '#FFFFFF',
            shadowColour: 'grey',
            shadowBlur: 5
          }}
          preset // You can set this bool depending on whether you have a pre-selected colour in state.
          presetColour={this.state.selectedColour1}
          animated
        />
        </form>

          {/* <div
          onClick={this.clearColourWheel}
          style={{
            cursor: 'pointer',
            fontSize: 20,
            fontWeight: '500',
            color: '#000000',
            marginTop: 20
          }}>
          clear
        </div> */}
        <TextBefore store={this.state}/>
      </div>
    )
  }
}

export default TextAfter
