import React, { Component } from 'react'


 class TextAfter extends Component {
  state={
    text:'',
    hed:'3px',
    fontSize:'',
    textColor:'',
  }
  hendelonChange=(event)=>{
    this.setState({ [event.target.id]: event.target.value});
  }
hedOnclick=()=>{
  if(this.state.hed=='3px'){
    this.setState({hed:"5px"})
  }else
  this.setState({hed:"3px"})
 }
  render() {

    return (
      <div>
        <form className="boxInput">
          <br />
          <label>text</label>
          <br />
          <input
            type="text"
            id="text"
            name="text"
            onChange={this.hendelonChange}
            value={this.state.text}
          />
          <br />
          <br />
          <label>height</label>
          <br />
          <input 
          type="number" 
          name="hed"
           onClick={this.hedOnclick}
            value={this.state.hed} />
          <br />
          <br />
          <label>fontSize</label>
          <br />
          <input type="number"
           id="fontSize"
           name="fontSize"
           onChange={this.hendelonChange}
            value={this.state.fontSize} />
          <br />  
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor" className="inputColor" value={this.state.textColor}onChange={this.hendelonChange} />
          <input type="text" className='textColor' id="textColors" value={this.state.textColor} onChange={this.hendelonChange}/>
          </div>
        </form>
      </div>
    )
  }
}

export default TextAfter
