import React, { Component } from 'react';
import ColourWheel from 'react-colour-wheel';


 class TextAfter extends Component {
  state={
    text:'',
    hed:3,
    fontSize:'',
    selectedColour:'',
  }
  hendelonChange=(event)=>{
    this.setState({ [event.target.id]: event.target.value});
  }
hedOnclick=()=>{
  if(this.state.hed==3){
    this.setState({hed:5})
  }else
  this.setState({hed:3})
 }
  render() {

    return (
      <div>
        <form className="boxInput">
          <br />
          <label>text</label>
          <br />
          <input
            type="text"
            id="text"
            name="text"
            onChange={this.hendelonChange}
            value={this.state.text}
          />
          <br />
          <br />
          <label>height</label>
          <br />
          <input 
          type="number" 
          name="hed"
           onClick={this.hedOnclick}
           defaultValue={this.state.hed} />
          <br />
          <br />
          <label>fontSize</label>
          <br />
          <input type="number"
           id="fontSize"
           name="fontSize"
           onChange={this.hendelonChange}
            value={this.state.fontSize} />
          <br />  
          <br/>               
          <label>color</label>
          <br />
          <ColourWheel
  radius={200}
  lineWidth={50}
  onColourSelected={(rgb) => this.setState({ selectedColour: rgb })}
/>
        </form>
      </div>
    )
  }
}

export default TextAfter
