import React, { Component } from 'react'
import Style from 'style-it';

 class TextMiddle extends Component {
   constructor(props){
     super(props)
     this.state={
      hed2:'',
    textColor2:'',
    textColors2:'',
     }
   }
   hendelonChange = (event) => {
    this.setState({ [event.target.id]: event.target.value});
};
hedOnclick=()=>{
  if(this.state.hed2==3){
    this.setState({hed2:5})
  }else
  this.setState({hed2:3})
 }
  render() {
    return (
      <Style>
      {`#pilo {
        transform: rotate(-28deg)skew(20deg);
        font-size: ${this.props.fontSize}px;
        text-shadow:${this.state.hed2};   
     }
        #pilo::after {
         content:"${this.props.text}";
         font-size: ${this.props.fontSize}px;
         text-shadow:${this.props.hed};

        }
        #pilo::before {
         content:"${this.state.text}";
         font-size: ${this.props.fontSize}px;
         text-shadow:${this.props.hed3};

         }

     `}
      <div>
      <div>
          <h1
            className="text"
            id="pilo" 
          >
            {this.state.text}
          </h1>
        </div>
        <form>
        <br />
          <label>height</label>
          <br />
          <input 
          type="number" 
          onChange={this.hendelonChange} 
            value={this.state.hed2} />
          <br /> 
          <br/>               
          <label>color</label>
          <br />
          <div className="color">
          <input type="color" id="textColor2"className="inputColor" value={this.state.textColor2}onChange={this.hendelonChange} />
          <input type="text" id="textColors2" className='textColor' value={this.state.textColor2} onChange={this.hendelonChange}/>
          </div>
        </form>
        <br/>
      </div>
      </Style>
    )
  }
}

export default TextMiddle
