import React from 'react';
import TextAfter from './component/TextAfter';
import ColourWheel from "./colourWheel/ColourWheel";


function App() {
  return (
    <div >
       <ColourWheel
              radius={200}
              padding={10}
              lineWidth={50}
              onColourSelected={(rgb) => this.setState({ selectedColour: rgb })}
              onRef={(ref) => (this.colourWheel = ref)}
              spacers={{
                colour: "#FFFFFF",
                shadowColour: "grey",
                shadowBlur: 5,
              }}
              preset // You can set this bool depending on whether you have a pre-selected colour in state.
              presetColour={this.props.selectedColour}
              animated
            />
   <TextAfter/>
    </div>
  );
}

export default App;
